import React, { useState, useEffect } from 'react';
import { Container, Typography, FormControl, InputLabel, Select, MenuItem, Grid, Card, CardContent, TextField, Button, Paper, List, ListItem, ListItemText, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle } from '@mui/material';
import './App.css';

function App() {
    const [classrooms, setClassrooms] = useState([]);
    const [location, setLocation] = useState('');
    const [time, setTime] = useState('');
    const [day, setDay] = useState('');
    const [booking, setBooking] = useState({ classroomId: '', startTime: '', endTime: '' });
    const [teacherAuthenticated, setTeacherAuthenticated] = useState(false);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [loginOpen, setLoginOpen] = useState(true);

    useEffect(() => {
        const fetchData = [
            { id: 1, name: 'Room 101', location: 'AB1', time: '8am to 9am', day: 'Monday', capacity: 30, status: 'Available', availableTimes: ['8:00 AM - 9:00 AM', '9:00 AM - 10:00 AM'] },
            { id: 2, name: 'Room 102', location: 'AB2', time: '9am to 10am', day: 'Tuesday', capacity: 20, status: 'Available', availableTimes: ['9:00 AM - 10:00 AM', '10:00 AM - 11:00 AM'] },
            { id: 3, name: 'Lab 201', location: 'CB', time: '10am to 11am', day: 'Wednesday', capacity: 15, status: 'Available', availableTimes: ['10:00 AM - 11:00 AM', '11:00 AM - 12:00 PM'] },
            // More data...
        ];
        setClassrooms(fetchData);
    }, []);

    const handleLocationChange = (event) => setLocation(event.target.value);
    const handleTimeChange = (event) => setTime(event.target.value);
    const handleDayChange = (event) => setDay(event.target.value);
    const handleBookingChange = (e) => {
        const { name, value } = e.target;
        setBooking(prev => ({ ...prev, [name]: value }));
    };

    const handleBookingSubmit = (e) => {
        e.preventDefault();
        // Booking logic...
    };

    const handleLoginSubmit = (e) => {
        e.preventDefault();
        // Simple authentication simulation
        fetch('/api/authenticate', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ email, password }),
        })
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    setTeacherAuthenticated(true);
                    setLoginOpen(false);
                } else {
                    alert('Invalid email or password');
                }
            });
    };

    const getCurrentTimeSlotAndDay = () => {
      const now = new Date();
      const hours = now.getHours();
      const minutes = now.getMinutes();
      const dayIndex = now.getDay(); // 0 for Sunday, 1 for Monday, etc.

      const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
      const currentDay = daysOfWeek[dayIndex];

      const currentTime = `${hours < 10 ? '0' + hours : hours}:${minutes < 10 ? '0' + minutes : minutes}`;

      const timeSlots = [
          '8:00 AM - 9:00 AM',
          '9:00 AM - 10:00 AM',
          '10:00 AM - 11:00 AM',
          '11:00 AM - 12:00 PM',
          '12:00 PM - 1:00 PM',
          '1:00 PM - 2:00 PM',
          '2:00 PM - 3:00 PM',
          '3:00 PM - 4:00 PM',
          '4:00 PM - 5:00 PM',
          '5:00 PM - 6:00 PM',
          '6:00 PM - 7:00 PM',
          '7:00 PM - 8:00 PM',
      ];

      let currentSlot = null;

      for (let i = 0; i < timeSlots.length; i++) {
          const [start, end] = timeSlots[i].split(' - ');
          const startHour = parseInt(start.split(':')[0]) + (start.includes('PM') && start.split(':')[0] !== '12' ? 12 : 0);
          const endHour = parseInt(end.split(':')[0]) + (end.includes('PM') && end.split(':')[0] !== '12' ? 12 : 0);

          if (hours >= startHour && hours < endHour) {
              currentSlot = timeSlots[i];
              break;
          }
      }

      return { currentSlot, currentDay };
  };


    const handlePresentlyClick = () => {
        const currentSlot = getCurrentTimeSlotAndD();
        if (currentSlot) {
            setTime(currentSlot);
        }
    };

    const filteredClassrooms = classrooms.filter(room => {
        return (
            (!location || room.location === location) &&
            (!time || room.availableTimes.includes(time)) &&
            (!day || room.day === day)
        );
    });

    const timeSlots = [
        '8:00 AM - 9:00 AM',
        '9:00 AM - 10:00 AM',
        '10:00 AM - 11:00 AM',
        '11:00 AM - 12:00 PM',
        '12:00 PM - 1:00 PM',
        '1:00 PM - 2:00 PM',
        '2:00 PM - 3:00 PM',
        '3:00 PM - 4:00 PM',
        '4:00 PM - 5:00 PM',
        '5:00 PM - 6:00 PM',
        '6:00 PM - 7:00 PM',
        '7:00 PM - 8:00 PM',
    ];

    return (
        <Container className="App">
            <Typography variant="h4" gutterBottom>Available Classrooms and Labs</Typography>
            <Grid container spacing={2}>
                <Grid item xs={12} md={3}>
                    <FormControl fullWidth>
                        <InputLabel>Location</InputLabel>
                        <Select value={location} onChange={handleLocationChange}>
                            <MenuItem value=""><em>All Locations</em></MenuItem>
                            <MenuItem value="AB1">AB1</MenuItem>
                            <MenuItem value="AB2">AB2</MenuItem>
                            <MenuItem value="CB">CB</MenuItem>
                        </Select>
                    </FormControl>
                </Grid>
                <Grid item xs={12} md={3}>
                    <FormControl fullWidth>
                        <InputLabel>Time</InputLabel>
                        <Select value={time} onChange={handleTimeChange}>
                            <MenuItem value=""><em>All Times</em></MenuItem>
                            {timeSlots.map((slot, index) => (
                                <MenuItem key={index} value={slot}>{slot}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                </Grid>
                <Grid item xs={12} md={3}>
                    <FormControl fullWidth>
                        <InputLabel>Day</InputLabel>
                        <Select value={day} onChange={handleDayChange}>
                            <MenuItem value=""><em>All Days</em></MenuItem>
                            <MenuItem value="Monday">Monday</MenuItem>
                            <MenuItem value="Tuesday">Tuesday</MenuItem>
                            <MenuItem value="Wednesday">Wednesday</MenuItem>
                            <MenuItem value="Thursday">Thursday</MenuItem>
                            <MenuItem value="Friday">Friday</MenuItem>
                            <MenuItem value="Saturday">Saturday</MenuItem>
                            <MenuItem value="Sunday">Sunday</MenuItem>
                        </Select>
                    </FormControl>
                </Grid>
                <Grid item xs={12} md={3}>
                    <Button
                        variant="contained"
                        color="primary"
                        fullWidth
                        style={{ marginTop: '16px' }}
                        onClick={handlePresentlyClick}
                    >
                        PRESENTLY
                    </Button>
                </Grid>
            </Grid>

            <Paper className="classroom-list" elevation={3} style={{ marginTop: '20px' }}>
                <Typography variant="h6">Available Classrooms and Labs</Typography>
                <List>
                    {filteredClassrooms.map(classroom => (
                        <ListItem key={classroom.id}>
                            <ListItemText
                                primary={`${classroom.name} - ${classroom.location}`}
                                secondary={`Capacity: ${classroom.capacity} | Status: ${classroom.status} | Available Times: ${classroom.availableTimes.join(', ')}`}
                            />
                        </ListItem>
                    ))}
                </List>
            </Paper>

            {teacherAuthenticated ? (
                <Paper className="booking-form" elevation={3} style={{ marginTop: '20px' }}>
                    <Typography variant="h6">Book a Classroom</Typography>
                    <form onSubmit={handleBookingSubmit}>
                        <TextField
                            label="Classroom ID"
                            name="classroomId"
                            value={booking.classroomId}
                            onChange={handleBookingChange}
                            fullWidth
                            required
                        />
                        <TextField
                            label="Start Time"
                            name="startTime"
                            type="datetime-local"
                            value={booking.startTime}
                            onChange={handleBookingChange}
                            fullWidth
                            required
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="End Time"
                            name="endTime"
                            type="datetime-local"
                            value={booking.endTime}
                            onChange={handleBookingChange}
                            fullWidth
                            required
                            InputLabelProps={{ shrink: true }}
                        />
                        <Button type="submit" variant="contained" color="primary" fullWidth>
                            Book
                        </Button>
                    </form>
                </Paper>
            ) : (
                <Dialog open={loginOpen} onClose={() => setLoginOpen(false)}>
                    <DialogTitle>Teacher Login</DialogTitle>
                    <DialogContent>
                        <DialogContentText>
                            Please enter your college email and password to book a classroom.
                        </DialogContentText>
                        <TextField
                            autoFocus
                            margin="dense"
                            label="Email Address"
                            type="email"
                            fullWidth
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                        <TextField
                            margin="dense"
                            label="Password"
                            type="password"
                            fullWidth
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleLoginSubmit} color="primary">
                            Login
                        </Button>
                    </DialogActions>
                </Dialog>
            )}
        </Container>
    );
}

export default App;
