// src/App.js
import React, { useState, useEffect } from 'react';
import { Container, Typography, FormControl, InputLabel, Select, MenuItem, Grid, Button, Paper, List, ListItem, ListItemText, Snackbar } from '@mui/material';
import MuiAlert from '@mui/material/Alert';
import { Routes, Route, useNavigate } from 'react-router-dom';
import LoginPage from './components/LoginPage';
import BookingPage from './components/BookingPage';

function App() {
    const [classrooms, setClassrooms] = useState([]);
    const [location, setLocation] = useState('');
    const [time, setTime] = useState('');
    const [day, setDay] = useState('');
    const [floor, setFloor] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const navigate = useNavigate(); // Hook for navigation

    useEffect(() => {
        const fetchClassrooms = async () => {
            try {
                const response = await fetch('/api/classrooms');
                if (!response.ok) {
                    throw new Error('Failed to fetch classrooms');
                }
                const data = await response.json();
                setClassrooms(data);
            } catch (err) {
                setError('Error fetching classrooms');
                setSnackbarOpen(true);
                console.error('Error fetching classrooms:', err);
            }
        };
        fetchClassrooms();
    }, []);

    const handleLocationChange = (event) => setLocation(event.target.value);
    const handleTimeChange = (event) => setTime(event.target.value);
    const handleDayChange = (event) => setDay(event.target.value);
    const handleFloorChange = (event) => setFloor(event.target.value);

    const handlePresentlyClick = () => {
        const { currentSlot, currentDay } = getCurrentTimeSlotAndDay();
        if (currentSlot) {
            setTime(currentSlot);
            setDay(currentDay);
            setClassrooms(prevClassrooms => prevClassrooms.filter(room =>
                room.availableTimes.includes(currentSlot) &&
                room.day === currentDay
            ));
        }
    };

    const getCurrentTimeSlotAndDay = () => {
        const now = new Date();
        const hours = now.getHours();
        const dayIndex = now.getDay();

        const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
        const currentDay = daysOfWeek[dayIndex];

        const timeSlots = [
            '8:00 AM - 9:00 AM',
            '9:00 AM - 10:00 AM',
            '10:00 AM - 11:00 AM',
            '11:00 AM - 12:00 PM',
            '12:00 PM - 1:00 PM',
            '1:00 PM - 2:00 PM',
            '2:00 PM - 3:00 PM',
            '3:00 PM - 4:00 PM',
            '4:00 PM - 5:00 PM',
            '5:00 PM - 6:00 PM',
            '6:00 PM - 7:00 PM',
            '7:00 PM - 8:00 PM',
        ];

        let currentSlot = null;

        for (let i = 0; i < timeSlots.length; i++) {
            const [start, end] = timeSlots[i].split(' - ');
            const startHour = parseInt(start.split(':')[0]) + (start.includes('PM') && start.split(':')[0] !== '12' ? 12 : 0);
            const endHour = parseInt(end.split(':')[0]) + (end.includes('PM') && end.split(':')[0] !== '12' ? 12 : 0);

            if (hours >= startHour && hours < endHour) {
                currentSlot = timeSlots[i];
                break;
            }
        }

        return { currentSlot, currentDay };
    };

    const filteredClassrooms = classrooms.filter(room => {
        return (
            (!location || room.location === location) &&
            (!time || room.availableTimes.includes(time)) &&
            (!day || room.day === day) &&
            (!floor || room.floor === parseInt(floor))
        );
    });

    const timeSlots = [
        '8:00 AM - 9:00 AM',
        '9:00 AM - 10:00 AM',
        '10:00 AM - 11:00 AM',
        '11:00 AM - 12:00 PM',
        '12:00 PM - 1:00 PM',
        '1:00 PM - 2:00 PM',
        '2:00 PM - 3:00 PM',
        '3:00 PM - 4:00 PM',
        '4:00 PM - 5:00 PM',
        '5:00 PM - 6:00 PM',
        '6:00 PM - 7:00 PM',
        '7:00 PM - 8:00 PM',
    ];

    const getFloorsForLocation = (location) => {
        switch (location) {
            case 'AB1':
            case 'AB2':
                return Array.from({ length: 4 }, (_, i) => i + 1);
            case 'CB':
                return Array.from({ length: 10 }, (_, i) => i + 1);
            default:
                return [];
        }
    };

    const floors = getFloorsForLocation(location);

    const handleSnackbarClose = () => {
        setSnackbarOpen(false);
    };

    return (
        <Routes>
            <Route path="/login" element={<LoginPage />} />
            <Route path="/booking" element={<BookingPage />} />
            <Route path="/" element={
                <Container maxWidth="lg" style={{ padding: '20px' }}>
                    <Typography variant="h4" gutterBottom style={{ color: '#2196f3', textAlign: 'center' }}>Available Classrooms and Labs</Typography>

                    <Grid container spacing={3} alignItems="center" justifyContent="center">
                        {/* Location Filter */}
                        <Grid item xs={12} sm={6} md={3}>
                            <FormControl fullWidth>
                                <InputLabel style={{ color: '#2196f3' }}>Location</InputLabel>
                                <Select value={location} onChange={handleLocationChange}>
                                    <MenuItem value=""><em>All Locations</em></MenuItem>
                                    <MenuItem value="AB1">AB1</MenuItem>
                                    <MenuItem value="AB2">AB2</MenuItem>
                                    <MenuItem value="CB">CB</MenuItem>
                                </Select>
                            </FormControl>
                        </Grid>

                        {/* Floor Filter */}
                        <Grid item xs={12} sm={6} md={3}>
                            <FormControl fullWidth>
                                <InputLabel style={{ color: '#2196f3' }}>Floor</InputLabel>
                                <Select value={floor} onChange={handleFloorChange}>
                                    <MenuItem value=""><em>All Floors</em></MenuItem>
                                    {floors.map(floorNumber => (
                                        <MenuItem key={floorNumber} value={floorNumber}>{floorNumber}</MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                        </Grid>

                        {/* Time Filter */}
                        <Grid item xs={12} sm={6} md={3}>
                            <FormControl fullWidth>
                                <InputLabel style={{ color: '#2196f3' }}>Time</InputLabel>
                                <Select value={time} onChange={handleTimeChange}>
                                    <MenuItem value=""><em>All Times</em></MenuItem>
                                    {timeSlots.map((slot, index) => (
                                        <MenuItem key={index} value={slot}>{slot}</MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                        </Grid>

                        {/* Day Filter */}
                        <Grid item xs={12} sm={6} md={3}>
                            <FormControl fullWidth>
                                <InputLabel style={{ color: '#2196f3' }}>Day</InputLabel>
                                <Select value={day} onChange={handleDayChange}>
                                    <MenuItem value=""><em>All Days</em></MenuItem>
                                    <MenuItem value="Tuesday">Tuesday</MenuItem>
                                    <MenuItem value="Wednesday">Wednesday</MenuItem>
                                    <MenuItem value="Thursday">Thursday</MenuItem>
                                    <MenuItem value="Friday">Friday</MenuItem>
                                    <MenuItem value="Saturday">Saturday</MenuItem>
                                </Select>
                            </FormControl>
                        </Grid>

                        {/* Presently Button */}
                        <Grid item xs={12} style={{ textAlign: 'center' }}>
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={handlePresentlyClick}
                                style={{ fontSize: '18px', padding: '10px 20px', backgroundColor: '#4caf50', color: '#fff' }}
                            >
                                Presently
                            </Button>
                        </Grid>
                    </Grid>

                    {/* Classrooms List */}
                    <Paper style={{ marginTop: '20px', padding: '20px' }}>
                        <Typography variant="h6" gutterBottom style={{ color: '#2196f3' }}>Available Classrooms and Labs</Typography>
                        <List>
                            {filteredClassrooms.map(classroom => (
                                <ListItem key={classroom.id}>
                                    <ListItemText
                                        primary={<Typography style={{ color: '#000' }}>{`${classroom.name} - ${classroom.location} Floor ${classroom.floor}`}</Typography>}
                                        secondary={<Typography style={{ color: '#555' }}>{`Capacity: ${classroom.capacity} | Status: ${classroom.status} | Available Times: ${classroom.availableTimes.join(', ')}`}</Typography>}
                                    />
                                </ListItem>
                            ))}
                        </List>
                    </Paper>

                    {/* Book a Classroom Button */}
                    <Grid container justifyContent="center" style={{ marginTop: '20px' }}>
                        <Button
                            variant="contained"
                            color="secondary"
                            onClick={() => navigate('/booking')} // Navigate to booking page
                            style={{ fontSize: '18px', padding: '12px 24px', backgroundColor: '#f44336', color: '#fff' }}
                        >
                            Book a Classroom
                        </Button>
                    </Grid>

                    {/* Snackbar for Notifications */}
                    <Snackbar open={snackbarOpen} autoHideDuration={6000} onClose={handleSnackbarClose}>
                        <MuiAlert onClose={handleSnackbarClose} severity={error ? 'error' : 'success'}>
                            {error || success}
                        </MuiAlert>
                    </Snackbar>
                </Container>
            } />
        </Routes>
    );
}

export default App;
