import React, { useState, useEffect } from 'react';
import { Container, Typography, FormControl, InputLabel, Select, MenuItem, Grid, Button, Paper, List, ListItem, ListItemText, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle } from '@mui/material';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import './App.css';
import LoginPage from './LoginPage';

function App() {
    const [classrooms, setClassrooms] = useState([]);
    const [location, setLocation] = useState('');
    const [time, setTime] = useState('');
    const [day, setDay] = useState('');
    const [dateDialogOpen, setDateDialogOpen] = useState(false);
    const [timeDialogOpen, setTimeDialogOpen] = useState(false);

    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = [
            // Sample data
            { id: 1, name: 'Room 101', location: 'AB1', time: '8am to 9am', day: 'Monday', capacity: 30, status: 'Available', availableTimes: ['8:00 AM - 9:00 AM', '9:00 AM - 10:00 AM'] },
            // More data...
        ];
        setClassrooms(fetchData);
    }, []);

    const handleLocationChange = (event) => setLocation(event.target.value);
    const handleTimeChange = (event) => setTime(event.target.value);
    const handleDayChange = (event) => setDay(event.target.value);

    const handlePresentlyClick = () => {
        const { currentSlot, currentDay } = getCurrentTimeSlotAndDay();
        setDay(currentDay);
        setTime(currentSlot);
        setDateDialogOpen(true);
    };

    const getCurrentTimeSlotAndDay = () => {
        const now = new Date();
        const hours = now.getHours();
        const minutes = now.getMinutes();
        const dayIndex = now.getDay(); // 0 for Sunday, 1 for Monday, etc.

        const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
        const currentDay = daysOfWeek[dayIndex];

        const timeSlots = [
            '8:00 AM - 9:00 AM',
            '9:00 AM - 10:00 AM',
            '10:00 AM - 11:00 AM',
            '11:00 AM - 12:00 PM',
            '12:00 PM - 1:00 PM',
            '1:00 PM - 2:00 PM',
            '2:00 PM - 3:00 PM',
            '3:00 PM - 4:00 PM',
            '4:00 PM - 5:00 PM',
            '5:00 PM - 6:00 PM',
            '6:00 PM - 7:00 PM',
            '7:00 PM - 8:00 PM',
        ];

        let currentSlot = null;

        for (let i = 0; i < timeSlots.length; i++) {
            const [start, end] = timeSlots[i].split(' - ');
            const startHour = parseInt(start.split(':')[0]) + (start.includes('PM') && start.split(':')[0] !== '12' ? 12 : 0);
            const endHour = parseInt(end.split(':')[0]) + (end.includes('PM') && end.split(':')[0] !== '12' ? 12 : 0);

            if (hours >= startHour && hours < endHour) {
                currentSlot = timeSlots[i];
                break;
            }
        }

        return { currentSlot, currentDay };
    };

    const filteredClassrooms = classrooms.filter(room => {
        return (
            (!location || room.location === location) &&
            (!time || room.availableTimes.includes(time)) &&
            (!day || room.day === day)
        );
    });

    return (
        <Container className="App">
            <Typography variant="h4" gutterBottom>Available Classrooms and Labs</Typography>
            <Grid container spacing={2}>
                <Grid item xs={12} md={3}>
                    <FormControl fullWidth>
                        <InputLabel>Location</InputLabel>
                        <Select value={location} onChange={handleLocationChange}>
                            <MenuItem value=""><em>All Locations</em></MenuItem>
                            <MenuItem value="AB1">AB1</MenuItem>
                            <MenuItem value="AB2">AB2</MenuItem>
                            <MenuItem value="CB">CB</MenuItem>
                        </Select>
                    </FormControl>
                </Grid>
                <Grid item xs={12} md={3}>
                    <FormControl fullWidth>
                        <InputLabel>Time</InputLabel>
                        <Select value={time} onChange={handleTimeChange}>
                            <MenuItem value=""><em>All Times</em></MenuItem>
                            {['8:00 AM - 9:00 AM', '9:00 AM - 10:00 AM', '10:00 AM - 11:00 AM', '11:00 AM - 12:00 PM', '12:00 PM - 1:00 PM', '1:00 PM - 2:00 PM', '2:00 PM - 3:00 PM', '3:00 PM - 4:00 PM', '4:00 PM - 5:00 PM', '5:00 PM - 6:00 PM', '6:00 PM - 7:00 PM', '7:00 PM - 8:00 PM'].map((slot, index) => (
                                <MenuItem key={index} value={slot}>{slot}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                </Grid>
                <Grid item xs={12} md={3}>
                    <FormControl fullWidth>
                        <InputLabel>Day</InputLabel>
                        <Select value={day} onChange={handleDayChange}>
                            <MenuItem value=""><em>All Days</em></MenuItem>
                            <MenuItem value="Tuesday">Tuesday</MenuItem>
                            <MenuItem value="Wednesday">Wednesday</MenuItem>
                            <MenuItem value="Thursday">Thursday</MenuItem>
                            <MenuItem value="Friday">Friday</MenuItem>
                            <MenuItem value="Saturday">Saturday</MenuItem>
                        </Select>
                    </FormControl>
                </Grid>
                <Grid item xs={12} md={3}>
                    <Button
                        variant="contained"
                        color="primary"
                        fullWidth
                        style={{ marginTop: '16px' }}
                        onClick={handlePresentlyClick}
                    >
                        PRESENTLY
                    </Button>
                </Grid>
            </Grid>

            <Paper className="classroom-list" elevation={3} style={{ marginTop: '20px' }}>
                <Typography variant="h6">Available Classrooms and Labs</Typography>
                <List>
                    {filteredClassrooms.map(classroom => (
                        <ListItem key={classroom.id}>
                            <ListItemText
                                primary={`${classroom.name} - ${classroom.location}`}
                                secondary={`Capacity: ${classroom.capacity} | Status: ${classroom.status} | Available Times: ${classroom.availableTimes.join(', ')}`}
                            />
                        </ListItem>
                    ))}
                </List>
            </Paper>

            <Button
                variant="contained"
                color="secondary"
                style={{ marginTop: '20px' }}
                onClick={() => navigate('/login')}
            >
                Book a Classroom
            </Button>

            {/* Date Dialog */}
            <Dialog open={dateDialogOpen} onClose={() => setDateDialogOpen(false)}>
                <DialogTitle>Select Date</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        Select the day for which you want to view available classrooms and labs.
                    </DialogContentText>
                    <FormControl fullWidth>
                        <InputLabel>Day</InputLabel>
                        <Select value={day} onChange={(e) => setDay(e.target.value)}>
                            <MenuItem value=""><em>All Days</em></MenuItem>
                            <MenuItem value="Tuesday">Tuesday</MenuItem>
                            <MenuItem value="Wednesday">Wednesday</MenuItem>
                            <MenuItem value="Thursday">Thursday</MenuItem>
                            <MenuItem value="Friday">Friday</MenuItem>
                            <MenuItem value="Saturday">Saturday</MenuItem>
                        </Select>
                    </FormControl>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setDateDialogOpen(false)} color="primary">Cancel</Button>
                    <Button onClick={() => { setDateDialogOpen(false); setTimeDialogOpen(true); }} color="primary">Next</Button>
                </DialogActions>
            </Dialog>

            {/* Time Dialog */}
            <Dialog open={timeDialogOpen} onClose={() => setTimeDialogOpen(false)}>
                <DialogTitle>Select Time</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        Select the time slot for which you want to view available classrooms and labs.
                    </DialogContentText>
                    <FormControl fullWidth>
                        <InputLabel>Time</InputLabel>
                        <Select value={time} onChange={(e) => setTime(e.target.value)}>
                            <MenuItem value=""><em>All Times</em></MenuItem>
                            {['8:00 AM - 9:00 AM', '9:00 AM - 10:00 AM', '10:00 AM - 11:00 AM', '11:00 AM - 12:00 PM', '12:00 PM - 1:00 PM', '1:00 PM - 2:00 PM', '2:00 PM - 3:00 PM', '3:00 PM - 4:00 PM', '4:00 PM - 5:00 PM', '5:00 PM - 6:00 PM', '6:00 PM - 7:00 PM', '7:00 PM - 8:00 PM'].map((slot, index) => (
                                <MenuItem key={index} value={slot}>{slot}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setTimeDialogOpen(false)} color="primary">Cancel</Button>
                    <Button onClick={() => setTimeDialogOpen(false)} color="primary">Submit</Button>
                </DialogActions>
            </Dialog>
        </Container>
    );
}

function AppWrapper() {
    return (
        <Router>
            <Routes>
                <Route path="/login" element={<LoginPage />} />
                <Route path="/" element={<App />} />
            </Routes>
        </Router>
    );
}

export default AppWrapper;
